{!REQUIRESCRIPT("/soap/ajax/15.0/apex.js")}

var ArrayIds={!GETRECORDIDS($ObjectType.FS_Post_Install_Marketing__c)};
if(ArrayIds.length==0)
alert("No Post Install Marketing Record is selected to copy");
else if(ArrayIds.length>1)
alert("You can't select more than one Post Install Marketing record");
else if(ArrayIds.length==1){
var r = confirm("Do you want to Copy this  Post Install Marketing record");
if (r == true) {
var res = sforce.apex.execute("FSCopyPostInstallActivity","ChaintoHqMarketing",
                             {recId:'{!Account.Id}',pmid:ArrayIds[0],Obj:'Account'});
alert(res);
}
}
