WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/9.0/connection.js")}
{!REQUIRESCRIPT("/soap/ajax/15.0/apex.js")}

var ArrayIds={!GETRECORDIDS($ObjectType.Flavor_Change__c)};
if(ArrayIds.length==0)
alert("No Post Flavor Change  Record is selected to cancel");
else if(ArrayIds.length>1)
alert("You can't select more than one Flavor Change  record");
else if(ArrayIds.length==1){
var r = confirm("Do you want to Cancel this  Flavor Change  record");
if (r == true) {
var res = sforce.apex.execute("FSCancelPostInstallActivity","FSCancelFlavourChange", 
{cancelId:ArrayIds[0]}); 

alert(res);
document.location.reload(true);
}
}
